import React, { useState, useRef, useEffect } from 'react';
import { useChat } from '../context/ChatContext';
import { api } from '../lib/auth';

const Chat = () => {
  const { msgs, setMsgs, pending, setPending, loading, setLoading, error, setError, resetChat } = useChat();
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [msgs]);

  useEffect(() => {
    if (loading) {
      setIsTyping(true);
      const timer = setTimeout(() => setIsTyping(false), 1000);
      return () => clearTimeout(timer);
    }
  }, [loading]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputValue.trim() || loading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setError('');

    const newUserMsg = { role: 'user', content: userMessage, timestamp: new Date() };
    setMsgs(prev => [...prev, newUserMsg]);

    try {
      setLoading(true);
      const response = await api.post('/chat', {
        message: userMessage,
        history: msgs.slice(-10),
        pending
      });

      if (response.data.success) {
        const { aiResponse, result, replyText } = response.data.data;

        if (result?.needMoreInfo) {
          setPending({ action: aiResponse.action, partialData: result.partialData, missing: result.needMoreInfo.missing });
          setMsgs(prev => [...prev, { role: 'assistant', content: result.needMoreInfo.prompt, timestamp: new Date() }]);
        } else {
          setPending(null);
          setMsgs(prev => [...prev, { role: 'assistant', content: replyText, timestamp: new Date() }]);
        }
      } else {
        setMsgs(prev => [...prev, { role: 'assistant', content: 'Sorry, I encountered an error. Please try again.', timestamp: new Date() }]);
      }
    } catch (err) {
      console.error('Chat error:', err);
      setError(err.response?.data?.message || 'Failed to send message');
      setMsgs(prev => [...prev, { role: 'assistant', content: 'Sorry, I encountered an error. Please try again.', timestamp: new Date() }]);
      setLoading(false);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const styles = {
    container: {
      minHeight: 'calc(100vh - 80px)',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    },
    wrapper: {
      maxWidth: '1000px',
      margin: '0 auto',
      background: 'rgba(255, 255, 255, 0.95)',
      borderRadius: '20px',
      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden',
      backdropFilter: 'blur(10px)',
      minHeight: 'calc(100vh - 120px)'
    },
    header: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '30px 20px',
      textAlign: 'center',
      color: 'white'
    },
    title: {
      fontSize: 'clamp(1.8rem, 4vw, 2.5rem)',
      fontWeight: 'bold',
      margin: '0 0 10px 0',
      textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
      lineHeight: '1.2'
    },
    subtitle: {
      fontSize: 'clamp(0.9rem, 2.5vw, 1.1rem)',
      opacity: 0.9,
      margin: 0,
      lineHeight: '1.4'
    },
    chatArea: {
      height: 'clamp(400px, 60vh, 600px)',
      overflowY: 'auto',
      padding: '20px',
      background: '#f8fafc'
    },
    messageContainer: {
      display: 'flex',
      marginBottom: '20px',
      animation: 'fadeIn 0.3s ease-in'
    },
    userMessage: {
      marginLeft: 'auto',
      maxWidth: '85%'
    },
    aiMessage: {
      marginRight: 'auto',
      maxWidth: '85%'
    },
    messageBubble: {
      padding: '15px 20px',
      borderRadius: '20px',
      position: 'relative',
      boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
      wordWrap: 'break-word',
      maxWidth: '100%'
    },
    userBubble: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      borderBottomRightRadius: '5px'
    },
    aiBubble: {
      background: 'white',
      color: '#333',
      borderBottomLeftRadius: '5px',
      border: '1px solid #e2e8f0'
    },
    messageContent: {
      fontSize: 'clamp(13px, 3vw, 14px)',
      lineHeight: '1.5',
      margin: '0 0 8px 0',
      wordBreak: 'break-word'
    },
    timestamp: {
      fontSize: '11px',
      opacity: 0.7,
      textAlign: 'right'
    },
    inputArea: {
      padding: '20px',
      background: 'white',
      borderTop: '1px solid #e2e8f0'
    },
    inputForm: {
      display: 'flex',
      gap: '15px',
      alignItems: 'flex-end',
      flexWrap: 'wrap'
    },
    inputWrapper: {
      flex: '1 1 300px',
      position: 'relative',
      minWidth: '250px'
    },
    textarea: {
      width: '100%',
      padding: '15px 20px',
      border: '2px solid #e2e8f0',
      borderRadius: '25px',
      fontSize: 'clamp(13px, 3vw, 14px)',
      resize: 'none',
      outline: 'none',
      transition: 'all 0.3s ease',
      minHeight: '50px',
      maxHeight: '120px',
      fontFamily: 'inherit',
      boxSizing: 'border-box'
    },
    textareaFocus: {
      borderColor: '#667eea',
      boxShadow: '0 0 0 3px rgba(102, 126, 234, 0.1)'
    },
    sendButton: {
      padding: '15px 25px',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      border: 'none',
      borderRadius: '25px',
      fontSize: 'clamp(13px, 3vw, 14px)',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      boxShadow: '0 4px 15px rgba(102, 126, 234, 0.3)',
      flexShrink: 0,
      minWidth: '120px',
      justifyContent: 'center'
    },
    sendButtonHover: {
      transform: 'translateY(-2px)',
      boxShadow: '0 6px 20px rgba(102, 126, 234, 0.4)'
    },
    sendButtonDisabled: {
      opacity: 0.6,
      cursor: 'not-allowed',
      transform: 'none'
    },
    typingIndicator: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      padding: '15px 20px',
      background: 'white',
      borderRadius: '20px',
      borderBottomLeftRadius: '5px',
      border: '1px solid #e2e8f0',
      maxWidth: '85%',
      marginRight: 'auto',
      marginBottom: '20px'
    },
    typingDots: {
      display: 'flex',
      gap: '4px'
    },
    dot: {
      width: '8px',
      height: '8px',
      borderRadius: '50%',
      background: '#667eea',
      animation: 'typing 1.4s infinite ease-in-out'
    },
    dot2: { animationDelay: '0.2s' },
    dot3: { animationDelay: '0.4s' },
    welcomeContainer: {
      textAlign: 'center',
      padding: 'clamp(20px, 5vw, 40px) 20px'
    },
    welcomeIcon: {
      width: 'clamp(60px, 15vw, 80px)',
      height: 'clamp(60px, 15vw, 80px)',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '50%',
      margin: '0 auto 20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      boxShadow: '0 10px 30px rgba(102, 126, 234, 0.3)'
    },
    welcomeTitle: {
      fontSize: 'clamp(18px, 4vw, 24px)',
      fontWeight: 'bold',
      color: '#333',
      margin: '0 0 15px 0',
      lineHeight: '1.3'
    },
    welcomeText: {
      color: '#666',
      margin: '0 0 25px 0',
      fontSize: 'clamp(14px, 3vw, 16px)',
      lineHeight: '1.4'
    },
    exampleContainer: {
      display: 'flex',
      flexDirection: 'column',
      gap: '10px',
      alignItems: 'center',
      maxWidth: '100%'
    },
    example: {
      padding: '8px 16px',
      background: 'rgba(102, 126, 234, 0.1)',
      color: '#667eea',
      borderRadius: '20px',
      fontSize: 'clamp(12px, 2.5vw, 14px)',
      border: '1px solid rgba(102, 126, 234, 0.2)',
      textAlign: 'center',
      maxWidth: '90%',
      wordBreak: 'break-word'
    },
    errorContainer: {
      background: '#fef2f2',
      border: '1px solid #fecaca',
      borderRadius: '10px',
      padding: '15px',
      margin: '20px',
      color: '#dc2626',
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      fontSize: 'clamp(13px, 3vw, 14px)'
    },
    // Mobile-specific styles
    mobileContainer: {
      padding: '10px'
    },
    mobileWrapper: {
      borderRadius: '15px',
      minHeight: 'calc(100vh - 100px)'
    },
    mobileHeader: {
      padding: '20px 15px'
    },
    mobileChatArea: {
      height: 'clamp(350px, 50vh, 500px)',
      padding: '15px'
    },
    mobileInputArea: {
      padding: '15px'
    },
    mobileInputForm: {
      flexDirection: 'column',
      gap: '10px'
    },
    mobileInputWrapper: {
      flex: '1 1 auto',
      minWidth: 'auto'
    },
    mobileSendButton: {
      width: '100%',
      justifyContent: 'center'
    }
  };

  // Responsive breakpoints
  const isMobile = window.innerWidth <= 768;
  const isTablet = window.innerWidth <= 1024;

  const getResponsiveStyles = () => {
    if (isMobile) {
      return {
        container: { ...styles.container, ...styles.mobileContainer },
        wrapper: { ...styles.wrapper, ...styles.mobileWrapper },
        header: { ...styles.header, ...styles.mobileHeader },
        chatArea: { ...styles.chatArea, ...styles.mobileChatArea },
        inputArea: { ...styles.inputArea, ...styles.mobileInputArea },
        inputForm: { ...styles.inputForm, ...styles.mobileInputForm },
        inputWrapper: { ...styles.inputWrapper, ...styles.mobileInputWrapper },
        sendButton: { ...styles.sendButton, ...styles.mobileSendButton }
      };
    }
    return styles;
  };

  const responsiveStyles = getResponsiveStyles();

  return (
    <div style={responsiveStyles.container}>
      <div style={responsiveStyles.wrapper}>
        {/* Header */}
        <div style={responsiveStyles.header}>
          <h1 style={responsiveStyles.title}>AI Todo Assistant</h1>
          <p style={responsiveStyles.subtitle}>Your intelligent task management companion</p>
        </div>

        {/* Chat Area */}
        <div style={responsiveStyles.chatArea}>
          {msgs.length === 0 ? (
            <div style={responsiveStyles.welcomeContainer}>
              <div style={responsiveStyles.welcomeIcon}>
                <svg width="40" height="40" fill="none" stroke="currentColor" viewBox="0 0 24 24" style={{color: 'white'}}>
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                </svg>
              </div>
              <h3 style={responsiveStyles.welcomeTitle}>Welcome to your AI Assistant!</h3>
              <p style={responsiveStyles.welcomeText}>I can help you manage your tasks. Try saying:</p>
              <div style={responsiveStyles.exampleContainer}>
                <div style={responsiveStyles.example}>"Add a task to buy groceries"</div>
                <div style={responsiveStyles.example}>"Show me all my tasks"</div>
                <div style={responsiveStyles.example}>"Mark the grocery task as completed"</div>
              </div>
            </div>
          ) : (
            msgs.map((msg, index) => (
              <div
                key={index}
                style={{
                  ...responsiveStyles.messageContainer,
                  ...(msg.role === 'user' ? responsiveStyles.userMessage : responsiveStyles.aiMessage)
                }}
              >
                <div
                  style={{
                    ...responsiveStyles.messageBubble,
                    ...(msg.role === 'user' ? responsiveStyles.userBubble : responsiveStyles.aiBubble)
                  }}
                >
                  <p style={responsiveStyles.messageContent}>{msg.content}</p>
                  <div style={responsiveStyles.timestamp}>{formatTime(msg.timestamp)}</div>
                </div>
              </div>
            ))
          )}

          {/* Typing Indicator */}
          {isTyping && (
            <div style={responsiveStyles.typingIndicator}>
              <div style={responsiveStyles.typingDots}>
                <div style={responsiveStyles.dot}></div>
                <div style={{...responsiveStyles.dot, ...responsiveStyles.dot2}}></div>
                <div style={{...responsiveStyles.dot, ...responsiveStyles.dot3}}></div>
              </div>
              <span style={{color: '#666', fontSize: '14px'}}>AI is typing...</span>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* Input Area */}
        <div style={responsiveStyles.inputArea}>
          <form onSubmit={handleSubmit} style={responsiveStyles.inputForm}>
            <div style={responsiveStyles.inputWrapper}>
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="Try: 'Add a task' or just type naturally..."
                style={{
                  ...responsiveStyles.textarea,
                  ...(inputValue.trim() ? responsiveStyles.textareaFocus : {})
                }}
                rows="1"
                disabled={loading}
              />
            </div>
            <button
              type="submit"
              disabled={!inputValue.trim() || loading}
              style={{
                ...responsiveStyles.sendButton,
                ...(inputValue.trim() && !loading ? {} : responsiveStyles.sendButtonDisabled)
              }}
              onMouseEnter={(e) => {
                if (inputValue.trim() && !loading) {
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 6px 20px rgba(102, 126, 234, 0.4)';
                }
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'none';
                e.target.style.boxShadow = '0 4px 15px rgba(102, 126, 234, 0.3)';
              }}
            >
              {loading ? (
                <>
                  <div style={{
                    width: '16px',
                    height: '16px',
                    border: '2px solid white',
                    borderTop: '2px solid transparent',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite'
                  }}></div>
                  <span>Processing...</span>
                </>
              ) : (
                <>
                  <svg width="16" height="16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                  <span>Send</span>
                </>
              )}
            </button>
          </form>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div style={responsiveStyles.errorContainer}>
          <svg width="20" height="20" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <span>{error}</span>
        </div>
      )}

      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes typing {
          0%, 60%, 100% { transform: translateY(0); }
          30% { transform: translateY(-10px); }
        }
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        /* Responsive adjustments */
        @media (max-width: 768px) {
          .chat-container {
            padding: 10px;
          }
          .chat-wrapper {
            border-radius: 15px;
          }
          .chat-header {
            padding: 20px 15px;
          }
          .chat-area {
            height: 50vh;
            padding: 15px;
          }
          .input-area {
            padding: 15px;
          }
        }

        @media (max-width: 480px) {
          .chat-container {
            padding: 5px;
          }
          .chat-wrapper {
            border-radius: 10px;
          }
          .chat-header {
            padding: 15px 10px;
          }
          .chat-area {
            height: 45vh;
            padding: 10px;
          }
          .input-area {
            padding: 10px;
          }
        }
      `}</style>
    </div>
  );
};

export default Chat;
