import React, { createContext, useContext, useEffect, useState } from 'react';

const ChatStateContext = createContext(null);

const LS_KEY_MSGS = 'chat_msgs_v1';
const LS_KEY_PENDING = 'chat_pending_v1';

export function ChatProvider({ children }) {
  const [msgs, setMsgs] = useState([]);
  const [pending, setPending] = useState(null);

  useEffect(() => {
    try {
      const m = localStorage.getItem(LS_KEY_MSGS);
      const p = localStorage.getItem(LS_KEY_PENDING);
      if (m) setMsgs(JSON.parse(m));
      if (p) setPending(JSON.parse(p));
    } catch (_) {}
  }, []);

  useEffect(() => {
    try { localStorage.setItem(LS_KEY_MSGS, JSON.stringify(msgs)); } catch (_) {}
  }, [msgs]);

  useEffect(() => {
    try {
      if (pending) localStorage.setItem(LS_KEY_PENDING, JSON.stringify(pending));
      else localStorage.removeItem(LS_KEY_PENDING);
    } catch (_) {}
  }, [pending]);

  const value = { msgs, setMsgs, pending, setPending };
  return <ChatStateContext.Provider value={value}>{children}</ChatStateContext.Provider>;
}

export function useChatState() {
  const ctx = useContext(ChatStateContext);
  if (!ctx) throw new Error('useChatState must be used within ChatProvider');
  return ctx;
}
