import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Chat from './pages/Chat';
import Todos from './pages/Todos';
import { getToken, logout } from './lib/auth';

function PrivateRoute({ children }) {
  const token = getToken();
  return token ? children : <Navigate to="/login" replace />;
}

export default function App() {
  const token = getToken();

  return (
    <Router>
      <div className="min-h-screen bg-gray-50 text-gray-900">
        <nav className="bg-white shadow">
          <div className="max-w-5xl mx-auto px-4 py-3 flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link to="/" className="font-semibold">AI Chatbot</Link>
              {token && (
                <>
                  <Link to="/chat" className="text-sm">Chat</Link>
                  <Link to="/todos" className="text-sm">Todos</Link>
                </>
              )}
            </div>
            <div className="flex items-center gap-3">
              {!token ? (
                <>
                  <Link to="/login" className="text-sm">Login</Link>
                  <Link to="/register" className="text-sm">Register</Link>
                </>
              ) : (
                <button onClick={() => { logout(); window.location.href = '/login'; }} className="text-sm">Logout</button>
              )}
            </div>
          </div>
        </nav>

        <main className="max-w-5xl mx-auto p-4">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/chat" element={<PrivateRoute><Chat /></PrivateRoute>} />
            <Route path="/todos" element={<PrivateRoute><Todos /></PrivateRoute>} />
            <Route path="/" element={<Navigate to={token ? '/chat' : '/login'} replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}
