import React, { useState, useRef, useEffect } from 'react';
import { useChat } from '../context/ChatContext';
import { api } from '../lib/auth';
import './Chat.css';

const Chat = () => {
  const { msgs, addMsg, loading, setLoading, error, setError } = useChat();
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [msgs]);

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      hour12: true 
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputValue.trim() || loading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    addMsg({ role: 'user', content: userMessage, timestamp: new Date() });
    setIsTyping(true);
    setLoading(true);
    setError(null);

    try {
      const response = await api.post('/chat', { message: userMessage });

      // Handle different response formats
      let assistantMessage;
      if (response.data.message) {
        assistantMessage = response.data.message;
      } else if (response.data.data && response.data.data.message) {
        assistantMessage = response.data.data.message;
      } else if (response.data.success === false) {
        assistantMessage = response.data.message || 'Action failed. Please try again.';
      } else {
        assistantMessage = 'Action completed successfully!';
      }

      addMsg({ 
        role: 'assistant', 
        content: assistantMessage, 
        timestamp: new Date() 
      });
    } catch (err) {
      console.error('Chat error:', err);
      setError(err.response?.data?.message || 'Failed to send message');
      addMsg({ 
        role: 'assistant', 
        content: 'Sorry, I encountered an error. Please try again.', 
        timestamp: new Date() 
      });
    } finally {
      setIsTyping(false);
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const quickActions = [
    { text: 'Add a task', icon: '‚ûï' },
    { text: 'List tasks', icon: 'üìã' },
    { text: 'Mark completed', icon: '‚úÖ' },
    { text: 'Delete task', icon: 'üóëÔ∏è' }
  ];

  return (
    <div className="chat-container">
      {/* Left Sidebar */}
      <div className="sidebar">
        {/* Logo */}
        <div className="logo">
          <div className="logo-icon">‚ú¶</div>
        </div>

        {/* Create Button */}
        <div className="create-btn">
          <div className="create-icon">+</div>
        </div>

        {/* Navigation */}
        <nav className="nav-menu">
          <div className="nav-item active">
            <div className="nav-icon">üîç</div>
            <span>Home</span>
          </div>
          <div className="nav-item">
            <div className="nav-icon">üåê</div>
            <span>Discover</span>
          </div>
          <div className="nav-item">
            <div className="nav-icon">‚äû</div>
            <span>Spaces</span>
          </div>
        </nav>

        {/* User Account */}
        <div className="user-account">
          <div className="profile-icon">J</div>
          <div className="account-info">
            <span>Account</span>
            <div className="pro-badge">pro</div>
          </div>
          <div className="dropdown-icon">‚åÑ</div>
        </div>

        {/* Actions */}
        <div className="actions">
          <div className="action-item">
            <div className="action-icon">‚Üë</div>
            <span>Upgrade</span>
          </div>
          <div className="action-item">
            <div className="action-icon">‚Üì</div>
            <span>Install</span>
            <div className="notification-dot"></div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Chat Messages */}
        <div className="chat-messages">
          {msgs.length === 0 ? (
            <div className="welcome-screen">
              <div className="welcome-icon">‚ú¶</div>
              <h1>AI Todo Assistant</h1>
              <p>Your intelligent task management companion</p>
              <div className="quick-actions">
                {quickActions.map((action, index) => (
                  <button
                    key={index}
                    className="quick-action-btn"
                    onClick={() => setInputValue(action.text)}
                  >
                    <span className="action-icon">{action.icon}</span>
                    {action.text}
                  </button>
                ))}
              </div>
            </div>
          ) : (
            msgs.map((msg, index) => (
              <div
                key={index}
                className={`message ${msg.role === 'user' ? 'user-message' : 'assistant-message'}`}
              >
                <div className="message-avatar">
                  {msg.role === 'user' ? 'üë§' : '‚ú¶'}
                </div>
                <div className="message-content">
                  <div className="message-text">{msg.content}</div>
                  <div className="message-time">{formatTime(msg.timestamp)}</div>
                </div>
              </div>
            ))
          )}

          {isTyping && (
            <div className="message assistant-message typing">
              <div className="message-avatar">‚ú¶</div>
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* AI Input Box */}
        <div className="ai-input-container">
          <div className="ai-input-box">
            <div className="input-left-icons">
              <div className="input-icon active">üîç</div>
              <div className="input-icon">‚äû</div>
              <div className="input-icon">‚ùì</div>
            </div>

            <form onSubmit={handleSubmit} className="input-form">
              <input
                ref={inputRef}
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="Ask anything or @mention a Space"
                className="ai-input"
                disabled={loading}
              />
            </form>

            <div className="input-right-icons">
              <div className="input-icon">‚äû</div>
              <div className="input-icon">üåê</div>
              <div className="input-icon">üìé</div>
              <div className="input-icon">üé§</div>
              <button
                type="submit"
                onClick={handleSubmit}
                disabled={!inputValue.trim() || loading}
                className="send-button"
              >
                <div className="send-icon">‚ñÆ</div>
              </button>
            </div>
          </div>
        </div>

        {/* Bottom Right Utilities */}
        <div className="bottom-utilities">
          <div className="utility-icon">‚äûA+</div>
          <div className="utility-icon">‚ùì</div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
