import React, { useState } from 'react';
import { api } from '../lib/auth';

export default function Chat() {
  const [input, setInput] = useState('');
  const [msgs, setMsgs] = useState([]); // { role: 'user'|'assistant', content }
  const [pending, setPending] = useState(null); // { action, partialData, missing }
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function send(e) {
    e?.preventDefault();
    const text = input.trim();
    if (!text) return;

    const history = msgs.map(m => ({ role: m.role, content: m.content }));
    setMsgs(prev => [...prev, { role: 'user', content: text }]);
    setInput('');
    setLoading(true);
    setError('');

    try {
      const payload = { message: text, history };
      if (pending) payload.pending = pending;
      const { data } = await api.post('/chat', payload);
      const reply = data?.data?.replyText || 'Okay.';

      const need = data?.data?.result?.needMoreInfo;
      if (need) {
        setPending({ action: pending?.action || data?.data?.aiResponse?.action || 'create_todo', partialData: need.partialData, missing: need.missing });
      } else {
        setPending(null);
      }

      setMsgs(prev => [...prev, { role: 'assistant', content: reply }]);
    } catch (err) {
      setError(err?.response?.data?.message || 'Failed to process message');
    } finally {
      setLoading(false);
    }
  }

  function onKeyDown(e) {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      send(e);
    }
  }

  return (
    <div className="grid" style={{ gap: '1rem' }}>
      <div className="card" style={{ maxWidth: 860, margin: '0 auto', width: '100%' }}>
        <div style={{ maxHeight: '55vh', overflowY: 'auto', paddingBottom: '.5rem' }}>
          {msgs.map((m, i) => (
            <div key={i} style={{ display: 'flex', justifyContent: m.role === 'user' ? 'flex-end' : 'flex-start', margin: '.5rem 0' }}>
              <div className="border" style={{ background: m.role === 'user' ? '#111827' : '#ffffff', color: m.role === 'user' ? '#fff' : '#111827', padding: '.5rem .75rem', borderRadius: '.5rem', maxWidth: '85%' }}>
                {m.content}
              </div>
            </div>
          ))}
          {loading && (
            <div style={{ display: 'flex', justifyContent: 'flex-start', margin: '.5rem 0' }}>
              <div className="border" style={{ background: '#ffffff', color: '#6b7280', padding: '.5rem .75rem', borderRadius: '.5rem' }}>Assistant is typing…</div>
            </div>
          )}
        </div>

        <form onSubmit={send} className="grid" style={{ gap: '.5rem' }}>
          <textarea className="input" rows={3} placeholder={pending ? 'Answer the question (e.g., buy milk)' : 'Try: "Add a task"'} value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={onKeyDown} />
          {error && <div style={{ color: 'crimson', fontSize: '.875rem' }}>{error}</div>}
          <button className="btn" disabled={loading}>{loading ? 'Thinking...' : 'Send'}</button>
          {pending && (
            <div style={{ fontSize: '.875rem', color: '#6b7280' }}>Waiting for: {pending.missing?.join(', ')}.</div>
          )}
        </form>
      </div>
    </div>
  );
}
