import React, { useState } from 'react';
import { api } from '../lib/auth';

export default function Chat() {
  const [message, setMessage] = useState('');
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function sendMessage(e) {
    e.preventDefault();
    if (!message.trim()) return;
    setLoading(true);
    setError('');
    try {
      const { data } = await api.post('/chat', { message });
      setResponses((prev) => [data.data, ...prev]);
      setMessage('');
    } catch (err) {
      setError(err?.response?.data?.message || 'Failed to process message');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="grid" style={{ gap: '1rem' }}>
      <div className="card">
        <form onSubmit={sendMessage} className="grid" style={{ gap: '.5rem' }}>
          <textarea className="input" rows={3} placeholder={"Type a command, e.g. 'Add a task'"} value={message} onChange={(e) => setMessage(e.target.value)} />
          {error && <div style={{ color: 'crimson', fontSize: '.875rem' }}>{error}</div>}
          <button className="btn" disabled={loading}>{loading ? 'Thinking...' : 'Send'}</button>
        </form>
      </div>

      {responses.map((r, idx) => (
        <div className="card" key={idx}>
          <div style={{ fontSize: '.875rem', color: '#6b7280' }}>Original:</div>
          <pre className="border" style={{ padding: '.5rem', whiteSpace: 'pre-wrap' }}>{r.originalMessage}</pre>
          <div style={{ fontSize: '.875rem', color: '#6b7280' }}>AI Parsed:</div>
          <pre className="border" style={{ padding: '.5rem', whiteSpace: 'pre-wrap' }}>{JSON.stringify(r.aiResponse, null, 2)}</pre>
          <div style={{ fontSize: '.875rem', color: '#6b7280' }}>Result:</div>
          <pre className="border" style={{ padding: '.5rem', whiteSpace: 'pre-wrap' }}>{JSON.stringify(r.result, null, 2)}</pre>
        </div>
      ))}
    </div>
  );
}
