import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { api, setToken } from '../lib/auth';

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const { data } = await api.post('/auth/login', { email, password });
      if (data?.data?.token) {
        setToken(data.data.token);
        navigate('/chat');
      }
    } catch (err) {
      setError(err?.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="max-w-5xl mx-auto">
      <div className="card" style={{ maxWidth: 420, margin: '3rem auto' }}>
        <h2 style={{ marginTop: 0 }}>Login</h2>
        <form onSubmit={handleSubmit} className="grid" style={{ gap: '.75rem' }}>
          <input className="input" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <input className="input" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          {error && <div style={{ color: 'crimson', fontSize: '.875rem' }}>{error}</div>}
          <button className="btn" disabled={loading}>{loading ? 'Loading...' : 'Login'}</button>
        </form>
        <div style={{ marginTop: '.5rem' }}>
          No account? <Link to="/register">Register</Link>
        </div>
      </div>
    </div>
  );
}
