import React, { useEffect, useState } from 'react';
import { api } from '../lib/auth';

export default function Todos() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function fetchTodos() {
    try {
      const { data } = await api.get('/todos');
      setTodos(data.data || []);
    } catch (err) {
      setError('Failed to load todos');
    }
  }

  useEffect(() => {
    fetchTodos();
  }, []);

  async function addTodo(e) {
    e.preventDefault();
    if (!title.trim()) return;
    setLoading(true);
    try {
      const { data } = await api.post('/todos', { title });
      setTodos([data.data, ...todos]);
      setTitle('');
    } catch (err) {
      setError('Failed to add todo');
    } finally {
      setLoading(false);
    }
  }

  async function toggle(todo) {
    try {
      const { data } = await api.put(`/todos/${todo._id}/toggle`);
      setTodos(todos.map(t => t._id === todo._id ? data.data : t));
    } catch (err) {
      setError('Failed to toggle');
    }
  }

  async function remove(todo) {
    try {
      await api.delete(`/todos/${todo._id}`);
      setTodos(todos.filter(t => t._id !== todo._id));
    } catch (err) {
      setError('Failed to delete');
    }
  }

  return (
    <div className="grid" style={{ gap: '1rem' }}>
      <div className="card">
        <form onSubmit={addTodo} className="grid" style={{ gap: '.5rem' }}>
          <input className="input" placeholder="New task title" value={title} onChange={(e) => setTitle(e.target.value)} />
          <button className="btn" disabled={loading}>{loading ? 'Adding...' : 'Add Task'}</button>
        </form>
      </div>

      {error && <div style={{ color: 'crimson' }}>{error}</div>}

      <div className="grid" style={{ gap: '.75rem' }}>
        {todos.map(todo => (
          <div className="card" key={todo._id}>
            <div className="flex items-center justify-between">
              <div>
                <div style={{ fontWeight: 600 }}>{todo.title}</div>
                <div style={{ fontSize: '.875rem', color: '#6b7280' }}>{todo.status}{todo.isCompleted ? ' • Completed' : ''}</div>
              </div>
              <div className="flex items-center gap-3">
                <button className="btn" onClick={() => toggle(todo)}>{todo.isCompleted ? 'Undo' : 'Complete'}</button>
                <button className="btn" onClick={() => remove(todo)} style={{ background: '#991b1b' }}>Delete</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
